// Lấy phrase của ngày hôm nay dựa trên ngày trong năm
export const getTodaysPhrase = () => {
  const now = new Date();
  const start = new Date(now.getFullYear(), 0, 0);
  const diff = now - start;
  const oneDay = 1000 * 60 * 60 * 24;
  const dayOfYear = Math.floor(diff / oneDay);
  
  // Sử dụng modulo để lặp lại khi hết danh sách
  const index = dayOfYear % nomenVerbVerbindungen.length;
  return nomenVerbVerbindungen[index];
};

// Lấy tổng số cụm từ
export const getTotalPhrases = () => nomenVerbVerbindungen.length;

export default nomenVerbVerbindungen;
